(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{485:function(v,t,_){"use strict";_.r(t);var e=_(11),r=Object(e.a)({},(function(){var v=this,t=v.$createElement,_=v._self._c||t;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"_20210918-库布勒罗斯改变曲线-kubler-ross-change-curve"}},[v._v("20210918 库布勒罗斯改变曲线(Kubler-Ross change Curve)")]),v._v(" "),_("p",[v._v("库布勒罗斯改变曲线（Kubler-Ross change Curve) 的原型是上世纪60年代心理学家伊丽莎白·库布勒—罗斯提出的悲伤阶段模型。这个模型是用于解释人们在面临死亡时的心理历程的。最初的悲伤五个阶段模型包括否认期、愤怒期、协议期、犹豫期和接收期。")]),v._v(" "),_("p",[v._v("按照模型这五个阶段是一个逐渐演变的过程。当人们得知自己即将面临死亡的时候，第一反应是震惊不能接受，从而否认这个事实。继而对这个事实感到愤怒，为什么是我？不公平！然后开始承认了这个事实，并希望通过自己的积极行为来缓解症状。但当意识到自己面临死亡越来越近的时候，心情低落，郁郁寡欢。最后终于全面接受了自己的死亡事实，并开始做好准备。")]),v._v(" "),_("p",[v._v("主要包括三个阶段："),_("strong",[v._v("震惊和否认阶段、愤怒和抑郁阶段、接受接纳阶段")])]),v._v(" "),_("h3",{attrs:{id:"阶段一：震惊和否认阶段。"}},[v._v("阶段一：震惊和否认阶段。")]),v._v(" "),_("p",[v._v("改变带来的第一个反应是短暂的震惊，习惯于有更多可靠性的保证和知道的人，这时会因为缺乏信息、害怕未知或者害怕自己做出错误或愚蠢的决定，而采取保守的态度。")]),v._v(" "),_("p",[v._v("震惊过后个体会本能的否认。或许是因为不想改变现状，或许是感觉到了威胁，或者仅仅害怕失败而否认改变。如果从没有经历过重大改变的人是很难从这个阶段走出做出改变。")]),v._v(" "),_("h3",{attrs:{id:"阶段二：愤怒和抑郁阶段"}},[v._v("阶段二：愤怒和抑郁阶段")]),v._v(" "),_("p",[v._v("但变革或改变并不会因为否认和消失。继而个体会不自觉地寻找替罪羊，通过指责一些人或事来发泄怒火，达到缓解内在焦虑和害怕的情绪。")]),v._v(" "),_("p",[v._v("曲线显示的最低点是现实变革或变化给个体带来的打击，使其情绪由愤怒转向自我怀疑和极度的焦虑抑郁的状态。")]),v._v(" "),_("h3",{attrs:{id:"阶段三：接受接纳阶段"}},[v._v("阶段三：接受接纳阶段")]),v._v(" "),_("p",[v._v("经过了黑暗的情绪期后，个体开始正视现实，接受变革和变化的不可避免性。可能意识到变化可能是新机遇，或者已经适应了变化，甚至可能迫不及待地等待着变化的进展。")]),v._v(" "),_("p",[v._v("到了这个阶段，个体对未来的改变预期更加坚定。相信并期待着改变预期的到来。")])])}),[],!1,null,null,null);t.default=r.exports}}]);